// Keep only rows where Date === today (in any of these formats)
// - "dd/mm/yyyy" or "d/m/yy"
// - "yyyy-mm-dd"
// - Google Sheets serial number (when returning unformatted values)
// and Sentiment is empty

const pad = n => String(n).padStart(2, '0');

function ymdFromDate(d) {
  return `${d.getFullYear()}-${pad(d.getMonth()+1)}-${pad(d.getDate())}`;
}

function normalizeToYMD(val) {
  if (val == null) return null;

  // Case 1: number -> Google Sheets serial date (days since 1899-12-30)
  if (typeof val === 'number' && !Number.isNaN(val)) {
    const ms = Math.round((val - 25569) * 86400 * 1000);
    const d = new Date(ms);
    return ymdFromDate(d);
  }

  // Case 2: Date object
  if (val instanceof Date) {
    return ymdFromDate(val);
  }

  // Case 3: string formats
  const s = String(val).trim();

  // 3a) dd/mm/yyyy or d/m/yy
  let m = s.match(/^(\d{1,2})[\/\-](\d{1,2})[\/\-](\d{2}|\d{4})$/);
  if (m) {
    let day = parseInt(m[1], 10);
    let mon = parseInt(m[2], 10);
    let year = parseInt(m[3], 10);
    if (year < 100) year = 2000 + year; // assume 20xx for 2-digit years
    return `${year}-${pad(mon)}-${pad(day)}`;
  }

  // 3b) yyyy-mm-dd (already Y-M-D)
  m = s.match(/^(\d{4})-(\d{1,2})-(\d{1,2})/);
  if (m) {
    return `${m[1]}-${pad(m[2])}-${pad(m[3])}`;
  }

  return null; // unknown format
}

// Today's date (uses n8n workflow timezone)
const now = new Date();
const todayYMD = ymdFromDate(now);

const out = [];
for (const item of items) {
  const j = item.json || {};
  const dateYMD = normalizeToYMD(j.Date);
  const sentiment = (j.Sentiment || '').toString().trim();
  if (dateYMD === todayYMD && sentiment === '') {
    out.push({ json: j });
  }
}
return out;

const out = [];
for (const item of items) {
  const j = item.json || {};
  let content = '';
  try {
    content = j?.body?.choices?.[0]?.message?.content ?? j?.content ?? j?.body ?? '';
  } catch (e) { content = ''; }

  let parsed = {};
  if (typeof content === 'string') {
    try { parsed = JSON.parse(content); } catch (e) { parsed = {}; }
  } else if (content && typeof content === 'object') {
    parsed = content;
  }

  j.Sentiment = (parsed.sentiment || 'neutral');
  j.Confidence = (parsed.confidence != null ? parsed.confidence : 0.5);
  if (!parsed.sentiment) j._parse_error = true;

  out.push({ json: j });
}
return out;

// Build counts/percentages for today's rows and emit a single summary item
const pad = n => String(n).padStart(2, '0');
function ymdFromDate(d){ return `${d.getFullYear()}-${pad(d.getMonth()+1)}-${pad(d.getDate())}`; }
function normalizeToYMD(val){
  if (val == null) return null;
  if (typeof val === 'number' && !Number.isNaN(val)) {
    const ms = Math.round((val - 25569) * 86400 * 1000);
    return ymdFromDate(new Date(ms));
  }
  if (val instanceof Date) return ymdFromDate(val);
  const s = String(val).trim();
  let m = s.match(/^(\d{1,2})[\/\-](\d{1,2})[\/\-](\d{2}|\d{4})$/);
  if (m) {
    let day = parseInt(m[1], 10), mon = parseInt(m[2], 10), year = parseInt(m[3], 10);
    if (year < 100) year = 2000 + year;
    return `${year}-${pad(mon)}-${pad(day)}`;
  }
  m = s.match(/^(\d{4})-(\d{1,2})-(\d{1,2})/);
  if (m) return `${m[1]}-${pad(m[2])}-${pad(m[3])}`;
  return null;
}

const todayYMD = ymdFromDate(new Date());
const counts = { positive: 0, neutral: 0, negative: 0 };
let total = 0;

for (const it of items) {
  const j = it.json || {};
  const d = normalizeToYMD(j.Date);
  if (d === todayYMD) {
    let s = (j.Sentiment || 'neutral').toString().toLowerCase().trim();
    if (!(s in counts)) s = 'neutral';
    counts[s] += 1; total += 1;
  }
}

const pct = n => total ? Math.round((n/total)*100) : 0;
return [{
  json: {
    date: todayYMD, total,
    positive: counts.positive, neutral: counts.neutral, negative: counts.negative,
    pct_positive: pct(counts.positive), pct_neutral: pct(counts.neutral), pct_negative: pct(counts.negative),
    labels: ["Positive","Neutral","Negative"],
    data: [counts.positive, counts.neutral, counts.negative]
  }
}];

